export type ValueType = string | number;
export type FieldNames = {
    value: ValueType;
    label: string;
    children: string;
};
export type CascaderItemType = {
    value: ValueType;
    label: string;
    disabled?: boolean;
    children?: CascaderItemType[];
};
export type NormalizeItem = CascaderItemType & {
    value: string;
    label: string;
    children?: NormalizeItem[];
};
export declare const useNormalizedItems: (fieldNames: FieldNames) => {
    normalizeItem: (item: CascaderItemType) => NormalizeItem;
    getSelectedItems: (items: CascaderItemType[], selectedValue: ValueType) => CascaderItemType[];
};
